import { StatusBar } from 'expo-status-bar';
import { useState } from 'react';
import { StyleSheet, Text, View, TouchableOpacity } from 'react-native';
import { linear } from 'react-native/Libraries/Animated/Easing';


var min = 1;
var max = 50;

export default function App() {
  const [buttoner, setButtoner] = useState(0)



  if (buttoner == 1) {
    return (
      <View style={styles.container}>
      <Text style={styles.instructions}>Press the button!</Text>
      <StatusBar style="auto" />

      <TouchableOpacity onPress={() => setButtoner(1)} style={styles.button_one}>
        <Text style={styles.button_text}>Generate Schedule</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => setButtoner(2)} style={styles.button_one}>
        <Text style={styles.button_text}>Add Task</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => setButtoner(3)} style={styles.button_one}>
        <Text style={styles.button_text}>View Tasks</Text>
      </TouchableOpacity>
    </View>
    );
  } 

  if (buttoner == 2) {
    return (
      <View style={styles.container}>
      <Text style={styles.instructions}>Press the button!</Text>
      <StatusBar style="auto" />

      <TouchableOpacity onPress={() => setButtoner(1)} style={styles.button_one}>
        <Text style={styles.button_text}>Generate Schedule</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => setButtoner(2)} style={styles.button_one}>
        <Text style={styles.button_text}>Add Task</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => setButtoner(3)} style={styles.button_one}>
        <Text style={styles.button_text}>View Tasks</Text>
      </TouchableOpacity>
    </View>
    );
  } 

  if (buttoner == 3) {
    return (
      <View style={styles.container}>
      <Text style={styles.instructions}>Press the button!</Text>
      <StatusBar style="auto" />

      <TouchableOpacity onPress={() => setButtoner(1)} style={styles.button_one}>
        <Text style={styles.button_text}>Generate Schedule</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => setButtoner(2)} style={styles.button_one}>
        <Text style={styles.button_text}>Add Task</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => setButtoner(3)} style={styles.button_one}>
        <Text style={styles.button_text}>View Tasks</Text>
      </TouchableOpacity>
    </View>
    );
  } 

  return (
    <View style={styles.container}>
      <Text style={styles.instructions}>Press the button!</Text>
      <StatusBar style="auto" />

      <TouchableOpacity onPress={() => setButtoner(1)} style={styles.button_one}>
        <Text style={styles.button_text}>Generate Schedule</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => setButtoner(2)} style={styles.button_one}>
        <Text style={styles.button_text}>Add Task</Text>
      </TouchableOpacity>
      <TouchableOpacity onPress={() => setButtoner(3)} style={styles.button_one}>
        <Text style={styles.button_text}>View Tasks</Text>
      </TouchableOpacity>
    </View>
  );
}



const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    background: 'linear-gradient'
  },
  instructions: {
    color: '#888',
    fontSize: 18,
    marginHorizontal: 15,
    marginBottom: 10,
  },
  button_one: {
    backgroundColor: '#bd1',
    padding: 20,
    borderRadius: 5,
    tintColor: '#a09'
  },
  button_text: {
    fontSize: 20,
    color: '#fff',
  },
});
